    Kotlin




        Marker marker = new Marker();
marker.setPosition(new LatLng(37.5670135, 126.9783740));
marker.setOnClickListener(overlay -> {
    Toast.makeText(context, "Marker clicked", Toast.LENGTH_SHORT).show();
    return true;
});
marker.setMap(naverMap);





